function update_cam()
	if g.stage == 1 then
		if p.x <240 and p.y < 136 then
			cam.tx=0 cam.ty = 0 cam.lrp = .3
		elseif p.x <240*2 and p.y < 110 then
			cam.tx=240 cam.ty=0 cam.lrp = .3
		else
			cam.lrp = .2
			--if p.y<136+68 and p.x>240 then cam.x = math.max(240,lerp(cam.x,p.x-120,cam.lrp)) else
			--cam.x=math.min(240*3,math.max(0,lerp(cam.x,p.x-120,cam.lrp))) end
			--if cam.x<240 then cam.y=math.min(136,math.max(136,lerp(cam.y,p.y-64,cam.lrp/10))) else
			--cam.y=math.min(136,math.max(0,lerp(cam.y,p.y-64,cam.lrp))) end
			if p.y<136+68 and p.x>240 and p.x<2*240 then cam.x = lerp(cam.x, math.max(240+1,p.x-120),cam.lrp) else
			cam.x=lerp(cam.x, math.min(240*3+1,math.max(0,p.x-120)),cam.lrp) end
			if p.x<240 then cam.y=lerp(cam.y, 137, cam.lrp) else
			cam.y=lerp(cam.y,math.min(136+1,math.max(0,p.y-64)),cam.lrp) end
			print(cam.y, 0,0)
			return
		end
	end
	cam.x = lerp(cam.x+.1, cam.tx, cam.lrp)
	cam.y = lerp(cam.y+.1, cam.ty, cam.lrp)
end

